public class LargestNoUsingTerninaryOperator {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 12;
		int b = 34; 
		int large = a > b ? a : b;   // ternary operator result needs to be stored
		
		System.out.println(large);
	}

}
