public class LargestNoUsingTerninaryOperator {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 12;
		int b = 34;
		int large = 0;

		large = a > b ? a : b;
		
		System.out.println(large);
	}

}
